SELECT NAZWISKO, SUBSTR(ETAT, 1, 2) || ID_PRAC AS KOD FROM PRACOWNICY;
SELECT NAZWISKO, TRANSLATE(NAZWISKO, 'KLM', 'XXX') AS WOJNA_LITEROM FROM PRACOWNICY;
SELECT NAZWISKO FROM PRACOWNICY WHERE INSTR(SUBSTR(NAZWISKO, 1, LENGTH(NAZWISKO)/2), 'L', 1, 1) > 0;
SELECT NAZWISKO, ROUND(PLACA_POD*115/100) AS PODWYZKA FROM PRACOWNICY;
SELECT NAZWISKO, PLACA_POD, PLACA_POD/5 AS INWESTYCJA, PLACA_POD/5 * POWER((1+(1/10)), 10) AS KAPITAL, PLACA_POD/5 * (POWER((1+(1/10)), 10)-1) AS ZYSK FROM PRACOWNICY;
SELECT NAZWISKO, ZATRUDNIONY, FLOOR(MONTHS_BETWEEN(TO_DATE('2000/01/01', 'YYYY/MM/DD'),ZATRUDNIONY)/12) AS STAZ_W_2000 FROM PRACOWNICY;
SELECT NAZWISKO, TO_CHAR(ZATRUDNIONY, 'MONTH, DD YYYY') AS DATA_ZATRUDNIENIA FROM PRACOWNICY WHERE ID_ZESP=20;
SELECT TO_CHAR(CURRENT_DATE, 'DAY') AS DZIS FROM DUAL;
SELECT NAZWA, ADRES, CASE ADRES WHEN 'PIOTROWO 3A' THEN 'NOWE MIASTO' WHEN 'WLODKOWICA 16' THEN 'GRUNWALD' ELSE 'STARE MIASTO' END AS DZIELNICA FROM ZESPOLY;
SELECT NAZWISKO, PLACA_POD, CASE WHEN PLACA_POD>480 THEN 'Powyżej' WHEN PLACA_POD<480 THEN 'Poniżej' ELSE 'Dokładnie' END || ' 480' AS PRÓG FROM PRACOWNICY ORDER BY PLACA_POD DESC;
SELECT NAZWISKO, PLACA_POD, DECODE(SIGN(PLACA_POD-480), -1, 'Poniżej', 0, 'Dokładnie', 1, 'Powyżej') || ' 480' FROM PRACOWNICY ORDER BY PLACA_POD DESC;