SELECT MIN(PLACA_POD) AS MINIMUM, MAX(PLACA_POD) AS MAKSIMUM, MAX(PLACA_POD)-MIN(PLACA_POD) AS RÓŻNICA FROM PRACOWNICY;
SELECT ETAT, AVG(PLACA_POD) AS SREDNIA FROM PRACOWNICY GROUP BY ETAT ORDER BY SREDNIA DESC;
SELECT COUNT(*) AS PROFESOROWIE FROM PRACOWNICY WHERE ETAT='PROFESOR' GROUP BY ETAT;
SELECT ID_ZESP, SUM(PLACA_POD+NVL(PLACA_DOD, 0)) AS SUMARYCZNE_PLACE FROM PRACOWNICY GROUP BY ID_ZESP ORDER BY ID_ZESP;
SELECT MAX(SUMA) AS MAKS_SUM_PLACA FROM (SELECT SUM(PLACA_POD+NVL(PLACA_DOD, 0)) AS SUMA FROM PRACOWNICY GROUP BY ID_ZESP);
SELECT ID_SZEFA, MIN(PLACA_POD) AS MINIMALNA FROM PRACOWNICY GROUP BY ID_SZEFA ORDER BY MINIMALNA DESC;
SELECT ID_ZESP, COUNT(*) AS ILU_PRACUJE FROM PRACOWNICY GROUP BY ID_ZESP ORDER BY ILU_PRACUJE DESC;
SELECT ID_ZESP, COUNT(*) AS ILU_PRACUJE FROM PRACOWNICY GROUP BY ID_ZESP HAVING COUNT(*)>3 ORDER BY ILU_PRACUJE DESC;
SELECT ID_PRAC FROM PRACOWNICY GROUP BY ID_PRAC HAVING COUNT(*)>1;
SELECT ETAT, AVG(PLACA_POD) AS ŚREDNIA, COUNT(*) AS LICZBA FROM PRACOWNICY WHERE EXTRACT(YEAR FROM ZATRUDNIONY) <= 1990 GROUP BY ETAT ORDER BY ETAT;
SELECT ID_ZESP, ETAT, ROUND(AVG(PLACA_POD+NVL(PLACA_DOD, 0))) AS SREDNIA, ROUND(MAX(PLACA_POD+NVL(PLACA_DOD, 0))) AS MAKSYMALNA FROM PRACOWNICY WHERE ETAT IN ('ASYSTENT', 'PROFESOR') GROUP BY ID_ZESP,ETAT ORDER BY ID_ZESP, ETAT;
SELECT EXTRACT(YEAR FROM ZATRUDNIONY) AS ROK, COUNT(*) AS ILU_PRACOWNIKOW FROM PRACOWNICY GROUP BY EXTRACT(YEAR FROM ZATRUDNIONY) ORDER BY ROK;
SELECT LENGTH(NAZWISKO) AS Ile_liter, COUNT(*) AS W_ilu_nazwiskach FROM PRACOWNICY GROUP BY LENGTH(NAZWISKO) ORDER BY Ile_liter;
SELECT SUM(CASE WHEN UPPER(NAZWISKO) LIKE '%A%' THEN 1 ELSE 0 END) AS Ile_nazwisk_z_A, SUM(CASE WHEN UPPER(NAZWISKO) LIKE '%E%' THEN 1 ELSE 0 END) AS Ile_nazwisk_z_E FROM PRACOWNICY;
SELECT ID_ZESP, SUM(PLACA_POD) AS SUMA_PLAC, LISTAGG(NAZWISKO || ':' || PLACA_POD, ',') WITHIN GROUP (ORDER BY NAZWISKO) AS PRACOWNICY FROM PRACOWNICY GROUP BY ID_ZESP ORDER BY ID_ZESP;