SELECT NAZWISKO, ETAT, ID_ZESP FROM PRACOWNICY WHERE ID_ZESP = (SELECT ID_ZESP FROM PRACOWNICY WHERE NAZWISKO='BRZEZINSKI');
SELECT NAZWISKO, ETAT, ZATRUDNIONY FROM PRACOWNICY WHERE ZATRUDNIONY = (SELECT MIN(ZATRUDNIONY) FROM PRACOWNICY WHERE ETAT='PROFESOR');
SELECT NAZWISKO, ZATRUDNIONY, ID_ZESP FROM PRACOWNICY WHERE ZATRUDNIONY IN (SELECT MAX(ZATRUDNIONY) FROM PRACOWNICY GROUP BY ID_ZESP) ORDER BY ZATRUDNIONY;
SELECT ID_ZESP, NAZWA, ADRES FROM ZESPOLY WHERE ID_ZESP NOT IN (SELECT ID_ZESP FROM PRACOWNICY);
SELECT z.ID_ZESP, z.NAZWA, z.ADRES FROM ZESPOLY z WHERE (SELECT COUNT(p.NAZWISKO) FROM PRACOWNICY p WHERE z.ID_ZESP=p.ID_ZESP) = 0;
SELECT p.NAZWISKO, p.PLACA_POD, p.ETAT FROM PRACOWNICY p WHERE PLACA_POD>(SELECT AVG(r.PLACA_POD) FROM PRACOWNICY r WHERE p.ETAT = r.ETAT GROUP BY r.ETAT);
SELECT p.NAZWISKO, p.PLACA_POD FROM PRACOWNICY p WHERE PLACA_POD>=0.75*(SELECT PLACA_POD FROM PRACOWNICY r WHERE r.ID_PRAC=p.ID_SZEFA);
SELECT NAZWISKO FROM PRACOWNICY WHERE ETAT='PROFESOR' AND ID_PRAC NOT IN (SELECT ID_SZEFA FROM PRACOWNICY WHERE ETAT='STAZYSTA');
SELECT ID_ZESP, SUMA FROM (SELECT MAX(SUM(PLACA_POD)) AS SUMA FROM PRACOWNICY GROUP BY ID_ZESP), PRACOWNICY GROUP BY ID_ZESP, SUMA HAVING SUMA = SUM(PLACA_POD);
SELECT NAZWISKO, PLACA_POD FROM PRACOWNICY, (SELECT MAX(PLACA_POD) AS PIERWSZA FROM PRACOWNICY, (SELECT MAX(PLACA_POD) AS DRUGA FROM PRACOWNICY, (SELECT MAX(PLACA_POD) AS TRZECIA FROM PRACOWNICY) WHERE PLACA_POD<TRZECIA) WHERE PLACA_POD<DRUGA) WHERE PLACA_POD>=PIERWSZA;
SELECT EXTRACT(YEAR FROM ZATRUDNIONY) AS ROK, COUNT(*) AS LICZBA FROM PRACOWNICY GROUP BY EXTRACT(YEAR FROM ZATRUDNIONY) ORDER BY LICZBA DESC;
SELECT EXTRACT(YEAR FROM ZATRUDNIONY) AS ROK, COUNT(*) AS LICZBA FROM (SELECT MAX(COUNT(*)) AS LICZBA1 FROM PRACOWNICY GROUP BY EXTRACT(YEAR FROM ZATRUDNIONY)), PRACOWNICY GROUP BY EXTRACT(YEAR FROM ZATRUDNIONY), LICZBA1 HAVING COUNT(*) = LICZBA1;
SELECT p.NAZWISKO, p.ETAT, p.PLACA_POD, NAZWA FROM PRACOWNICY p NATURAL JOIN ZESPOLY WHERE PLACA_POD<(SELECT AVG(PLACA_POD) FROM PRACOWNICY r WHERE p.ETAT=r.ETAT);
SELECT p.NAZWISKO, p.ETAT, p.PLACA_POD, (SELECT AVG(PLACA_POD) FROM PRACOWNICY r GROUP BY r.ETAT HAVING p.ETAT=r.ETAT) AS SREDNIA FROM PRACOWNICY p  WHERE PLACA_POD<(SELECT AVG(PLACA_POD) FROM PRACOWNICY r WHERE p.ETAT=r.ETAT) ORDER BY p.ETAT;
SELECT p.NAZWISKO, (SELECT COUNT(*) AS LICZBA FROM PRACOWNICY r WHERE r.ID_SZEFA = p.ID_PRAC GROUP BY ID_SZEFA) AS PODWLADNI FROM PRACOWNICY p WHERE p.ETAT='PROFESOR' AND (SELECT ADRES FROM ZESPOLY z WHERE z.ID_ZESP=p.ID_ZESP)='PIOTROWO 3A';
SELECT p.NAZWISKO, (SELECT AVG(PLACA_POD) FROM PRACOWNICY r WHERE p.ID_ZESP=r.ID_ZESP GROUP BY ID_ZESP) AS SREDNIA, (SELECT MAX(PLACA_POD) FROM PRACOWNICY) AS MAKSYMALNA FROM PRACOWNICY p WHERE p.ETAT='PROFESOR';
SELECT p.NAZWISKO, (SELECT NAZWA FROM ZESPOLY z WHERE z.ID_ZESP=p.ID_ZESP) AS ZESPOL FROM PRACOWNICY p;